import React, { Component } from "react";
import InputGroup from "./InputGroup";
import { Button } from "./Button";
import Education from "./Education";
import Experience from "./Experience";

let i = 1;

class CVWorkingMode extends Component {
  constructor(props) {
    super(props);

    this.state = {
      educationChildren: [<Education key={i} />],
    };
  }

  addEducationChild() {
    i += 1;
    this.setState({
      educationChildren: [
        ...this.state.educationChildren,
        <Education key={i} />,
      ],
    });
  }

  render() {
    return (
      <div className="flex justify-center w-full h-full">
        <div className="border-2 border-gray-600 w-3/4 h-full my-12 bg-white">
          {/* Personal Info */}

          <div className="m-6">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Personal Information{" "}
            </h1>
            <div className="flex gap-3 mt-3 w-full">
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="First Name"
                myHook="firstName"
              />
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Last Name"
                myHook="lastName"
              />
            </div>
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Job Title "
              myHook="jobTitle"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Phone Number"
              myHook="phoneNumber"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Email"
              myHook="email"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Address "
              myHook="address"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Description "
              myHook="description"
              myHeight="h-32"
            />
          </div>
          {/* Education */}
          <div className="m-6" id="myEd">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Education{" "}
            </h1>
            <button onClick={this.addEducationChild}> Test Btn</button>
            <div className="flex flex-col">
              {" "}
              {this.state.educationChildren}{" "}
            </div>
          </div>
          {/* Experience */}
          <div className="m-6">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Experience{" "}
            </h1>
            <div className="flex flex-col"> </div>
          </div>
        </div>{" "}
      </div>
    );
  }
}

export default CVWorkingMode;
