import React, { Component } from "react";
import Navbar from "./components/Navbar";
import CVWorkingMode from "./components/CVWorkingMode";
import CVPreviewMode from "./components/CVPreviewMode";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
    };
  }

  handleChange = (e) => {
    for (let key in this.state) {
      if (key == e.target.dataset.hook) {
        this.state[key] = e.target.value;
      } else {
        return;
      }
      console.log(this.state);
    }
  };

  render() {
    return (
      <div className="h-screen">
        <Navbar />
        <div className="flex">
          <CVWorkingMode testFn={this.handleChange} />
          <CVPreviewMode testState={this.state.firstName} />
        </div>
      </div>
    );
  }
}

export default App;
