import React, { Component } from "react";
import InputGroup from "./InputGroup";
import { Button } from "./Button";
import Education from "./Education";

class CVWorkingMode extends Component {
  constructor(props) {
    super(props);
  }

  onAddChild() {
    this.setState({
      numChildren: this.state.numChildren + 1,
    });
  }

  render() {
    let children = [];

    for (let i = 0; i < this.state.numChildren; i += 1) {
      children.push(<Education key={i} number={i} />);
    }

    return (
      <div className="flex justify-center w-full h-full">
        <div className="border-2 border-gray-600 w-3/4 h-full my-12 bg-white">
          {/* Personal Info */}

          <div className="m-6">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Personal Information{" "}
            </h1>
            <div className="flex gap-3 mt-3 w-full">
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="First Name"
                myHook="firstName"
              />
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Last Name"
                myHook="lastName"
              />
            </div>
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Job Title "
              myHook="jobTitle"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Phone Number"
              myHook="phoneNumber"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Email"
              myHook="email"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Address "
              myHook="address"
            />
            <InputGroup
              handleChangeMethod2={this.props.handleChangeMethod}
              inputTitle="Description "
              myHook="description"
              myHeight="h-32"
            />
          </div>
          {/* Education */}
          <div className="m-6" id="myEd">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Education{" "}
            </h1>
            <button className="p-4 bg-black text-white"> Test Append </button>
            <div className="flex flex-col"> {this.props.myChildren} </div>
          </div>
          {/* Experince */}
          <div className="m-6">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Experience{" "}
            </h1>
            <InputGroup inputTitle="Position " />
            <InputGroup inputTitle="Company " />
            <InputGroup inputTitle="Location " />

            <div className="flex gap-3 mt-3 w-full">
              <InputGroup inputTitle=" From" />
              <InputGroup inputTitle=" To" />
            </div>
            <div className="flex mt-3 justify-end gap-4">
              <Button title="Add" />
              <Button
                title="Delete"
                bgColor="bg-red-300"
                textColor="text-white"
                bgHover="hover:bg-sky-300"
              />
            </div>
          </div>
        </div>{" "}
      </div>
    );
  }
}

export default CVWorkingMode;
