import React, { Component } from "react";
import InputGroup from "./InputGroup";
import Education from "./Education";
import Experience from "./Experience";
import PreviewEducation from "./PreviewEducation";
import PVModePersonalDetails from "./PVModePersonalDetails";

let i = 1;
let j = 100;

class CVWorkingMode extends Component {
  constructor(props) {
    super(props);

    this.state = {
      educationChildren: [
        <Education
          key={i}
          number={i}
          addChild={this.addEducationChild}
          removeChild={this.removeEducationChild}
          handleChangeMethodEd={this.props.handleChangeMethod}
        />,
      ],
      experienceChildren: [
        <Experience
          key={j}
          number={j}
          addChild={this.addExperienceChild}
          removeChild={this.removeExperienceChild}
        />,
      ],
      previewEducation: [
        <PreviewEducation
          key={i}
          number={i}
          previewState={this.props.myStates}
          myInstName="Zabi"
        />,
      ],
    };

    this.addEducationChild = this.addEducationChild.bind(this);
    this.removeEducationChild = this.removeEducationChild.bind(this);

    this.addExperienceChild = this.addExperienceChild.bind(this);
    this.removeExperienceChild = this.removeExperienceChild.bind(this);
  }

  addEducationChild = () => {
    i += 1;
    this.setState({
      educationChildren: [
        ...this.state.educationChildren,
        <Education
          key={i}
          number={i}
          addChild={this.addEducationChild}
          removeChild={this.removeEducationChild}
          handleChangeMethodEd={this.props.handleChangeMethod}
        />,
      ],
      previewEducation: [
        ...this.state.previewEducation,
        <PreviewEducation key={i} />,
      ],
    });

    console.log(this.state.previewEducation);
  };

  addExperienceChild = () => {
    j += 1;
    this.setState({
      experienceChildren: [
        ...this.state.experienceChildren,
        <Experience
          key={j}
          number={j}
          addChild={this.addExperienceChild}
          removeChild={this.removeExperienceChild}
        />,
      ],
    });
  };

  removeEducationChild = (e) => {
    this.setState({
      educationChildren: this.state.educationChildren.filter(
        (el) => el.key !== e.target.id
      ),
      previewEducation: this.state.previewEducation.filter(
        (el) => el.key !== e.target.id
      ),
    });
  };

  removeExperienceChild = (e) => {
    this.setState({
      experienceChildren: this.state.experienceChildren.filter(
        (el) => el.key !== e.target.id
      ),
    });
  };

  render() {
    return (
      <div className="flex bg-myGray">
        {" "}
        {/* Working Mode  */}
        <div className="flex justify-center w-full h-full">
          <div className="border-2 border-gray-600 w-3/4 h-full my-12 bg-white">
            {/* Personal Info */}

            <div className="m-6">
              <h1 className="text-3xl font-bold underline underline-offset-4">
                {" "}
                Personal Information{" "}
              </h1>
              <div className="flex gap-3 mt-3 w-full">
                <InputGroup
                  handleChangeMethod2={this.props.handleChangeMethod}
                  inputTitle="First Name"
                  myHook="firstName"
                />
                <InputGroup
                  handleChangeMethod2={this.props.handleChangeMethod}
                  inputTitle="Last Name"
                  myHook="lastName"
                />
              </div>
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Job Title "
                myHook="jobTitle"
              />
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Phone Number"
                myHook="phoneNumber"
              />
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Email"
                myHook="email"
              />
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Address "
                myHook="address"
              />
              <InputGroup
                handleChangeMethod2={this.props.handleChangeMethod}
                inputTitle="Description "
                myHook="description"
                myHeight="h-32"
              />
            </div>
            {/* Education */}
            <div className="m-6" id="myEd">
              <h1 className="text-3xl font-bold underline underline-offset-4">
                {" "}
                Education{" "}
              </h1>

              <div className="flex flex-col">
                {" "}
                {this.state.educationChildren}{" "}
              </div>
            </div>
            {/* Experience */}
            <div className="m-6">
              <h1 className="text-3xl font-bold underline underline-offset-4">
                {" "}
                Experience{" "}
              </h1>
              <div className="flex flex-col">
                {" "}
                {this.state.experienceChildren}{" "}
              </div>
            </div>
          </div>{" "}
        </div>
        {/*  Preview Mode */}
        <div className="flex justify-center w-full h-full">
          <div className="border-2 border-gray-600 w-4/5 h-full my-12 bg-white">
            {/* This is the Header of the CVPreviewMode */}
            <div className="bg-slate-900 h-32 flex flex-col justify-center gap-3">
              <p className="text-white text-4xl px-6 font-bold font-cinzel">
                {" "}
                {this.props.myStates.firstName} {""}
                {this.props.myStates.lastName}
              </p>
              <p className="text-white text-2xl px-6 font-cinzel">
                {" "}
                {this.props.myStates.jobTitle}
              </p>
            </div>
            {/* Body of the CVPreviewMode */}

            <div className="flex h-full">
              <div className="flex flex-col space-y-8 m-5 bg-white w-4/6">
                {/* Description */}
                <div>
                  <h1 className="pvHeader"> Description </h1>
                  <p className="text-black text-lg py-3 italic">
                    {this.props.myStates.description}
                  </p>
                </div>

                {/* Education */}

                <div>
                  <h1 className="pvHeader"> Education </h1>
                  {this.state.previewEducation}
                </div>

                {/* Experience */}
                <div>
                  <h1 className="pvHeader"> Experience </h1>
                  <ul>
                    <li> - </li>
                  </ul>
                </div>
              </div>
              <PVModePersonalDetails myStates2={this.props.myStates} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CVWorkingMode;
