import React, { Component } from "react";
import PersonalInfo from "./PersonalInfo";
import Education from "./Education";
import Experience from "./Experience";

class CVWorkingMode extends Component {
  render() {
    return (
      <div className="flex justify-center bg-myGray">
        <div className="border-2 border-gray-600 w-2/5 h-full my-12 bg-white">
          {/* Personal Info */}
          <div className="m-6">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Personal Information{" "}
            </h1>
            <div className="flex gap-3 mt-3 w-full">
              <InputGroup inputTitle="First Name" />
              <InputGroup inputTitle="Last Name" />
            </div>
            <InputGroup inputTitle="Job Title " />
            <InputGroup inputTitle="Phone Number" />
            <InputGroup inputTitle="Email" />
            <InputGroup inputTitle="Address " />
            <InputGroup inputTitle="Description " myHeight="h-32" />
          </div>

          {/* Education */}
          <div className="m-6">
            <h1 className="text-3xl font-bold underline underline-offset-4">
              {" "}
              Education{" "}
            </h1>
            <InputGroup inputTitle="Institution Name " />
            <InputGroup inputTitle="Location " />
            <InputGroup inputTitle="Degree " />
            <InputGroup inputTitle="Subject " />
            <div className="flex gap-3 mt-3 w-full">
              <InputGroup inputTitle="From" />
              <InputGroup inputTitle="To" />
            </div>
            <div className="flex mt-3 justify-end gap-4">
              <Button title="Add" />
              <Button
                title="Delete"
                bgColor="bg-red-300"
                textColor="text-white"
                bgHover="hover:bg-sky-300"
              />
            </div>
          </div>

          {/* Experince */}
        </div>{" "}
      </div>
    );
  }
}

export default CVWorkingMode;
