import React, { Component } from "react";
import Navbar from "./components/Navbar";
import CVWorkingMode from "./components/CVWorkingMode";
import CVPreviewMode from "./components/CVPreviewMode";
import IconReactquery from "./components/React-icon";
import Education from "./components/Education";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      jobTitle: "",
      phoneNumber: "",
      email: "",
      address: "",
      description: "",
      numChildren: 1,
    };
  }

  handleChange = (e) => {
    for (let key in this.state) {
      if (key == e.target.dataset.hook) {
        this.setState({ [key]: e.target.value });
      }
    }
  };

  addChild() {
    this.setState({
      numChildren: this.state.numChildren + 1,
    });
  }

  render() {
    let children = [];

    for (let i = 0; i < this.state.numChildren; i += 1) {
      children.push(<Education key={i} number={i} />);
    }

    return (
      <div className="h-screen">
        <Navbar />
        <div className="flex bg-myGray">
          <CVWorkingMode handleChangeMethod={this.handleChange} />
          <CVPreviewMode myStates={this.state} />
        </div>
        <div className="flex justify-center items-center bg-slate-900 text-sky-300">
          <p className="text-xl p-3"> Copyright Â© 2023 skynter</p>
          <IconReactquery />
        </div>
      </div>
    );
  }
}

export default App;
